function RecipeApp() {
  // https://developer.edamam.com/ - where we getting the API
  const APP_ID = "0a6f51df";
  const APP_KEY = "e0dab08929946848bd8a70fa38f7dca3";

  const req = `https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}`;

  const getRecipes = async () => {
    const response = await axios.get(req);
    const data = await response.data;
    setRecipes(data.hits);
  };

  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    getRecipes();
  }, [recipes]);

  return (
    <div>
      <h1>Recipe App</h1>
      <RecipeSearch />
      <div>
        {recipes.map((recipe, i) => (
          <RecipeItem key={i} {...recipe} />
        ))}
      </div>
    </div>
  );
}

export default RecipeApp;
